function onChange(control, oldValue, newValue, isLoading, isTemplate) {
   if (isLoading || newValue === '') {
      return;
   }
   if( newValue.match(/^[0-9]+$/) != null)
   { 
	//if(newValue.indexOf('(') != 0) {
		//alert ("index "+newValue.indexOf('('));
var match = newValue.match(/^(\d{3})(\d{3})(\d{4})$/);

  if (match) {


		//var formatPhone=newValue.substring(0,3);
		//var formatPhone1=newValue.substring(3,6);
		//var formatPhone2=newValue.substring(6,11);
		var formatPhone4 = '(' + match[1] + ') ' + match[2] + '-' + match[3] ; //'('+formatPhone+') '+formatPhone1 +'-'+formatPhone2;
		//alert("newValue: " +formatPhone+') '+formatPhone1 +'-'+formatPhone2 );
		g_form.setValue('phone_number', formatPhone4);
     }
     else {alert ("Invalid phone number!");}
   }
else if( newValue.match(/^\([0-9][0-9][0-9]\) *[0-9][0-9][0-9][- ]?[0-9][0-9][0-9][0-9]/) == null || newValue < 10) { alert ("Not a valid phone number: " + newValue);}
     // return formatPhone;


   //Type appropriate comment here, and begin script below

}
